import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // âœ…
import { useAuth } from "../context/AuthContext"; // âœ…

const SignupPage = () => {
  const [formData, setFormData] = useState({
    username: "",             // âœ… was email
    password: "",
    secretWord: "",
    favoritePark: "",
    profileImage: null,
  });  
  const [parks, setParks] = useState([]);
  const [loading, setLoading] = useState(true);
  const { login } = useAuth(); // âœ… get login function
  const navigate = useNavigate(); // âœ… for redirecting

  // API key for the parks API (non-sensitive)
  const apiKey = "wT7qTdbCiApVc0O9U4sDpW0AEFgcfmyB8fHNW42O";

  useEffect(() => {
    const fetchAllParks = async () => {
      let allParks = [];
      let start = 0;
      const limit = 50;
      let total = 0;

      try {
        do {
          const res = await fetch(
            `https://developer.nps.gov/api/v1/parks?limit=${limit}&start=${start}&api_key=${apiKey}`
          );
          const data = await res.json();
          total = parseInt(data.total);
          allParks = [...allParks, ...data.data];
          start += limit;
        } while (allParks.length < total);

        setParks(allParks);
      } catch (err) {
        console.error("Error fetching parks:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAllParks();
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setFormData((prev) => ({
      ...prev,
      profileImage: file,
    }));
  };

  // Helper to convert a File to a Base64 string
  const getBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting form:", formData);

    // Convert profile image to Base64 if a file is selected
    let profileImageBase64 = null;
    if (formData.profileImage) {
      try {
        profileImageBase64 = await getBase64(formData.profileImage);
      } catch (error) {
        console.error("Error converting image:", error);
      }
    }

    // Map formData to backend fields.
    const signupData = {
      username: formData.email,        // email goes to username field
      password: formData.password,       // password will be hashed on the backend
      secret: formData.secretWord,       // secret word
      fav_park: formData.favoritePark,   // favorite park
      profile_image: profileImageBase64, // new field for profile image
    };

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/users`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(signupData),
      });

      if (!response.ok) {
        console.error("Error creating user:", response.statusText);
      } else {
        const data = await response.json();
        console.log("User created successfully!", data);
        login(data.userId); // âœ… store in AuthContext
        navigate("/account"); // âœ… redirect to account page
        // Optionally, clear the form or redirect the user
        setFormData({
          email: "",
          password: "",
          secretWord: "",
          favoritePark: "",
          profileImage: null,
        });
      }
    } catch (error) {
      console.error("Error submitting signup:", error);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-12 bg-white shadow-md rounded-lg p-6">
      <h2 className="text-2xl font-bold text-center mb-6 text-green-800">Sign Up</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
     {/* Username */}
<div>
  <label className="block font-medium mb-1 text-gray-700">Username</label>
  <input
    type="text"                                // âœ… was type="email"
    name="username"                            // âœ… was name="email"
    className="w-full px-3 py-2 border rounded-md bg-[#ecece5] focus:outline-none"
    value={formData.username}
    onChange={handleChange}
    required
  />
</div>

        {/* Password */}
        <div>
          <label className="block font-medium mb-1 text-gray-700">Password</label>
          <input
            type="password"
            name="password"
            className="w-full px-3 py-2 border rounded-md bg-[#ecece5] focus:outline-none"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        {/* Secret Word */}
        <div>
          <label className="block font-medium mb-1 text-gray-700">Secret Word</label>
          <input
            type="text"
            name="secretWord"
            className="w-full px-3 py-2 border rounded-md bg-[#ecece5] focus:outline-none"
            value={formData.secretWord}
            onChange={handleChange}
            required
          />
        </div>

        {/* Favorite National Park Dropdown */}
        <div>
          <label className="block font-medium mb-1 text-gray-700">Favorite National Park</label>
          {loading ? (
            <p>Loading parks...</p>
          ) : (
            <select
              name="favoritePark"
              className="w-full px-3 py-2 border rounded-md bg-[#ecece5] focus:outline-none"
              value={formData.favoritePark}
              onChange={handleChange}
              required
            >
              <option value="">-- Select a Park --</option>
              {parks
                .sort((a, b) => a.name.localeCompare(b.name))
                .map((park) => (
                  <option key={park.id} value={park.name}>
                    {park.name}
                  </option>
                ))}
            </select>
          )}
        </div>

        {/* Profile Image Upload */}
        <div className="text-center">
          <label className="block font-medium mb-2 text-gray-700">Profile Image</label>
          {formData.profileImage ? (
            <img
              src={URL.createObjectURL(formData.profileImage)}
              alt="Profile Preview"
              className="mx-auto mb-3 w-24 h-24 object-cover rounded-full border-2 border-green-700"
            />
          ) : (
            <div className="w-24 h-24 mx-auto mb-3 rounded-full border-2 border-dashed border-gray-400 flex items-center justify-center text-gray-400">
              <span>ðŸ“·</span>
            </div>
          )}
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="mx-auto block text-sm text-gray-500"
          />
        </div>

        {/* Submit Button */}
        <div className="text-center">
          <button
            type="submit"
            className="bg-green-800 text-white px-6 py-2 rounded-md hover:bg-green-900 transition"
          >
            Sign Up
          </button>
        </div>
      </form>
    </div>
  );
};

export default SignupPage;
